import { Component } from '@angular/core';
import { Cliente } from '../modelo/Cliente';
import { ClienteService } from '../servico/cliente.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {

    // Objeto do tipo Cliente
    cliente = new Cliente();

    // Variável para visibilidade dos botões
    btnCadastro:boolean = true;

    // Variável para visibilidade da tabela
    tabela:boolean = true;

    // JSON de clientes
    clientes:Cliente[] = [];

    // Construtor
    constructor(private servico:ClienteService){}

    // Método de seleção
    selecionar():void{
      this.servico.selecionar()
      .subscribe(retorno => this.clientes = retorno);
    }

    // Método de cadastro
    cadastrar():void{
      this.servico.cadastrar(this.cliente)
      .subscribe(retorno => { 

        // Cadastrar o cliente no vetor
        this.clientes.push(retorno); 

        // Limpar formulário
        this.cliente = new Cliente();

        // Mensagem
        alert('Cliente cadastrado com sucesso!');
      });
    }

    // Método para selecionar um cliente específico
    selecionarCliente(posicao:number):void{

      // Selecionar cliente no vetor
      this.cliente = this.clientes[posicao];

      // Visibilidade dos botões
      this.btnCadastro = false;

      // Visibilidade da tabela
      this.tabela = false;

    }

    // Método para editar clientes
    editar():void{

      this.servico.editar(this.cliente)
      .subscribe(retorno => {

        // Obter posiçao do vetor onde está o cliente
        let posicao = this.clientes.findIndex(obj => {
          return obj.id == retorno.id;
        });

        // Alterar os dados do cliente no vetor
        this.clientes[posicao] = retorno;

        // Limpar formulário
        this.cliente = new Cliente();

        // Visibilidade dos botões
        this.btnCadastro = true;

        // Visibilidade da tabela
        this.tabela = true;

        // Mensagem
        alert('Cliente alterado com sucesso!');

      });

    }

    // Método para remover clientes
    remover():void{

      this.servico.remover(this.cliente.id)
      .subscribe(retorno => {

        // Obter posiçao do vetor onde está o cliente
        let posicao = this.clientes.findIndex(obj => {
          return obj.id == this.cliente.id;
        });

        // Remover cliente do vetor
        this.clientes.splice(posicao, 1);

        // Limpar formulário
        this.cliente = new Cliente();

        // Visibilidade dos botões
        this.btnCadastro = true;

        // Visibilidade da tabela
        this.tabela = true;

        // Mensagem
        alert('Cliente removido com sucesso!');

      });

    }

    // Método para cancelar
    cancelar():void{

      // Limpar formulário
      this.cliente = new Cliente();

      // Visibilidade dos botões
      this.btnCadastro = true;

      // Visibilidade da tabela
      this.tabela = true;

    }

    // Método de inicialização
    ngOnInit(){
      this.selecionar();
    }

}
<div class="container mt-3">
    <div class="row">
      <div class="col-12 col-lg-12 col-md-12">
            <form class="p-1">
                <div class="mb-3 position-absolute top-50 start-0 translate-middle-y">
                    <img class="img-thumbnail" src="assets/andersonfelix.webp">
                </div>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Nome</label>
                    <input type="text"   placeholder="Nome"   class="form-control" name="name"   [(ngModel)]="cliente.name">                  
                    
                </div>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Idade</label>
                    <input type="number" placeholder="Idade"  class="form-control" name="age"  [(ngModel)]="cliente.age">
                    
                </div>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Cidade</label>
                    <input type="text"   placeholder="Cidade" class="form-control" name="city" [(ngModel)]="cliente.city">
                    
                </div>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">Estado</label>
                  <input type="text"   placeholder="Estado" class="form-control" name="state" [(ngModel)]="cliente.state">
                </div>

                <input type="button" value="Cadastrar"  *ngIf="btnCadastro"   class="btn btn-primary"   (click)="cadastrar()">
                <input type="button" value="Alterar"    *ngIf="!btnCadastro"  class="btn btn-warning"   (click)="editar()">
                <input type="button" value="Remover"    *ngIf="!btnCadastro"  class="btn btn-danger"    (click)="remover()">
                <input type="button" value="Cancelar"   *ngIf="!btnCadastro"  class="btn btn-secondary" (click)="cancelar()">
            </form>
        </div>
        
        
    </div>
    <div class="row mt-3">
        <div class="col-6 col-lg-12 col-md-12">
            <div class="col-6 col-lg-12 col-md-12">
                <table class="table" *ngIf="tabela">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Nome</th>
                            <th>Idade</th>
                            <th>Cidade</th>
                            <th>Estado</th>
                            <th>Selecionar</th>
                        </tr>
                    </thead>
                
                    <tbody>
                        <tr *ngFor="let c of clientes; index as i">
                            <td>{{i+1}}</td>
                            <td>{{c.name}}</td>
                            <td>{{c.age}}</td>
                            <td>{{c.city}}</td>
                            <td>{{c.state}}</td>
                            <td><button class="btn btn-success" (click)="selecionarCliente(i)">Selecionar</button></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</div>
